<?php require_once(ABSPATH.'wp-admin/includes/user.php' );class SupportListing{
	public function __construct() {
		add_action('template_redirect', array($this,'redirectMethod'), 1);
		
	}	public function redirectMethod()	{			error_reporting(0);			if($_REQUEST['smgt-json-api']=='supportstaff-listing')			{				$response=$this->supportstaff_listing();	 				if(is_array($response)){					echo json_encode($response);				}				else				{					header("HTTP/1.1 401 Unauthorized");				}				die();			}			if($_REQUEST['smgt-json-api']=='add-supportstaff')			{				$response=$this->add_supportstaff($_REQUEST);	 				if(is_array($response)){					echo json_encode($response);				}				else				{					header("HTTP/1.1 401 Unauthorized");				}				die();			}			if($_REQUEST['smgt-json-api']=='edit-supportstaff')			{				$response=$this->edit_supportstaff($_REQUEST);	 				if(is_array($response)){					echo json_encode($response);				}				else				{					header("HTTP/1.1 401 Unauthorized");				}				die();			}			if($_REQUEST['smgt-json-api']=='delete-supportstaff')			{				$school_obj = new School_Management($_REQUEST['current_user']);				if($school_obj->role=='admin'){					$response=$this->delete_supportstaff($_REQUEST);	 				}				if(is_array($response)){					echo json_encode($response);				}				else				{					header("HTTP/1.1 401 Unauthorized");				}				die();			}	}	public function supportstaff_listing()	{		$response=array();		$teacher_obj = new Smgt_Teacher;		$supportstaffdata =get_users(array('role'=>'supportstaff'));		if(!empty($supportstaffdata)){						$i=0;			foreach ($supportstaffdata as $retrieved_data){				$userimagedata=get_user_image($retrieved_data->ID);				if(empty($userimagedata['meta_value']))				{					$imageurl=get_option( 'smgt_teacher_thumb');				}				else				{					$imageurl=$userimagedata['meta_value'];				}				$result[$i]['ID']=$retrieved_data->ID;				$result[$i]['image']=$imageurl;				$result[$i]['name']=$retrieved_data->display_name;				$result[$i]['email']=$retrieved_data->user_email;				$i++;
			}				$response['status']=1;				$response['resource']=$result;				return $response;		}		else		{			$error['message']=__("Please Fill All Fields",'school-mgt');			$response['status']=0;			$response['resource']=$error;		}			return $response;
	}	public function add_supportstaff($data)	{		$response=array();		$teacher_obj = new Smgt_Teacher;		$role='supportstaff';				$firstname=$data['first_name'];			$lastname=$data['last_name'];			$userdata = array(			'user_login'=>$data['username'],						'user_nicename'=>NULL,			'user_email'=>$data['email'],			'user_url'=>NULL,			'display_name'=>$firstname." ".$lastname,			);			if($data['password'] != "")				$userdata['user_pass']=$data['password'];					$usermetadata=array('middle_name'=>$data['middle_name'],						'gender'=>$data['gender'],						'birth_date'=>$data['birth_date'],						'address'=>$data['address'],						'city'=>$data['city_name'],						'state'=>$data['state_name'],						'zip_code'=>$data['zip_code'],						'phone'=>$data['phone'],						'mobile_number'=>$data['mobile_number'],						'alternet_mobile_number'=>$data['alternet_mobile_number'],						'working_hour'=>$data['working_hour'],						'possition'=>$data['possition'],						'smgt_user_avatar'=>$data['smgt_user_avatar']);						if( !email_exists( $data['email'] ) && !username_exists( $data['username'] )) {			$result=add_newuser($userdata,$usermetadata,$firstname,$lastname,$role);		}		else		{			$message['message']=__("Username Or Emailid All Ready Exist.",'school-mgt');			$response['status']=0;			$response['resource']=$message;			return $response;		}		if($result)		{			$message['message']=__("Record successfully inserted!",'school-mgt');			$response['status']=1;			$response['resource']=$message;			return $response;		}		else		{			$error['message']=__("Please Fill All Fields",'school-mgt');			$response['status']=0;			$response['resource']=$error;		}			return $response;			}	public function edit_supportstaff($data)	{		$response=array();		$role='supportstaff';				$firstname=$data['first_name'];			$lastname=$data['last_name'];			$userdata = array(			'user_login'=>$data['username'],						'user_nicename'=>NULL,			'user_email'=>$data['email'],			'user_url'=>NULL,			'display_name'=>$firstname." ".$lastname,			);			if($data['password'] != "")				$userdata['user_pass']=$data['password'];			$userdata['ID']=$data['supportstaff_id'];			$usermetadata=array('middle_name'=>$data['middle_name'],						'gender'=>$data['gender'],						'birth_date'=>$data['birth_date'],						'address'=>$data['address'],						'city'=>$data['city_name'],						'state'=>$data['state_name'],						'zip_code'=>$data['zip_code'],												'phone'=>$data['phone'],						'mobile_number'=>$data['mobile_number'],						'alternet_mobile_number'=>$data['alternet_mobile_number'],						'working_hour'=>$data['working_hour'],						'possition'=>$data['possition'],						'smgt_user_avatar'=>$data['smgt_user_avatar'],						'attachment'=>$data['attachment']);							$user_id = wp_update_user($userdata);		$flag=0;		foreach($usermetadata as $key=>$val){					$returnans=update_user_meta( $data['supportstaff_id'], $key,$val );			if($returnans){				$returnval=$returnans;				$flag=1;			}		}		if($flag=1)		{			$message['message']=__("Record successfully Updated!",'school-mgt');			$response['status']=1;			$response['resource']=$message;			//return $response;		}		else		{			$error['message']=__("Please Fill All Fields",'school-mgt');			$response['status']=0;			$response['resource']=$error;		}			return $response;			}	public function delete_supportstaff($data)	{		$response=array();		if($data['supportstaff_id']!=0){			$result=delete_usedata($data['supportstaff_id']);			if($result)			{				$message=__("Record successfully Deleted!","school-mgt");				$response['status']=1;				$response['resource']=$message;				//return $response;			}			else			{				$message['message']=__("Records Not Delete","school-mgt");				$response['status']=0;				$response['resource']=$message;			}			return $response;					}		else			{				$error['message']=__("Please Fill All Fields",'school-mgt');				$response['status']=0;				$response['resource']=$error;			}			return $response;			}
} ?>